.gradient(@color: #ddd, @start: -10%, @stop: 5%) {
	@start: lightness(@color, @start);
	@stop: lightness(@color, @stop);
	
	background: @color;
	background: -webkit-gradient(linear,
	    			left bottom,
	    			left top,
	    			color-stop(0, @start),
	    			color-stop(1, @stop));
	background: -moz-linear-gradient(center bottom,
	    			@start 0%,
	    			@stop 100%);
}


